initCluster() {
  while true; do
    local output=$(
      set -x;

      /cockroach/cockroach init \
        --insecure \
        --host=cockroachdb-0.cockroachdb:26257
    2>&1);

    local exitCode="$?";
    echo $output;

    if [[ "$exitCode" == "0" || "$output" == *"cluster has already been initialized"* ]]
      then break;
    fi

    sleep 5;
  done
}
initCluster;
