args:
set -ex;
cd /home;
mkdir binaries;
mkdir -p tmp/binaries;
busybox --install -s binaries;
cp binaries/* tmp/binaries/;
cp /bin/busybox tmp/binaries;

tokenLen=$(kubectl get secret release-name-sn-platform-vault-unseal-keys -o jsonpath='{.data.vault-root}'|wc -m); until [ ${tokenLen} -gt 1 ]; do
  echo "current token is empty ! wait another 10s~"
  sleep 10;
  tokenLen=$(kubectl get secret release-name-sn-platform-vault-unseal-keys -o jsonpath='{.data.vault-root}'|wc -m);
done; echo "vault root token is ready"; rootToken=$(kubectl get secret release-name-sn-platform-vault-unseal-keys -o jsonpath='{.data.vault-root}'|base64 -d); echo $rootToken; echo $rootToken > /vault/rootToken; export ROOT_TOKEN=$rootToken; vault login $ROOT_TOKEN; until [ $? -eq 0 ]; do
   echo "vault is not ready now, wait another 5s~";
   sleep 5;
   vault login $ROOT_TOKEN;
done; echo "vault is ready~"; echo "Start init public key to secret"; publicKey=$(wget -qO- http://release-name-sn-platform-vault:8200/v1/identity/oidc/.well-known/keys | base64 | tr -d \\n); kubectl patch secret release-name-sn-platform-vault-public-key --type='json' -p='[{"op":"replace","path":"/data/publicKey","value":"'$publicKey'"}]' -n default; echo -ne "POST /quitquitquit HTTP/1.1\r\nHost: 127.0.0.1:15020\r\nUser-Agent: curl/7.68.0\r\nAccept: */*\r\n\r\n"|nc 127.0.0.1:15020 || true;

kubectl delete secret release-name-sn-platform-vault-secret-env-injection --ignore-not-found=true -n default && kubectl delete secret release-name-sn-platform-vault-console-admin-passwd --ignore-not-found=true -n default; until [ $? -eq 0 ]; do
   echo "Clear vault resource not ready now, wait another 5s~";
   sleep 5;
   kubectl delete secret release-name-sn-platform-vault-secret-env-injection --ignore-not-found=true -n default && kubectl delete secret release-name-sn-platform-vault-console-admin-passwd --ignore-not-found=true -n default;
done; echo "Clear vault resource is ready~"; echo -ne "POST /quitquitquit HTTP/1.1\r\nHost: 127.0.0.1:15020\r\nUser-Agent: curl/7.68.0\r\nAccept: */*\r\n\r\n"|nc 127.0.0.1:15020 || true;

until bin/pulsar zookeeper-shell -server release-name-sn-platform-zookeeper:2181 get /admin/clusters/release-name-sn-platform; do
  sleep 3;
done;

set -e; brokerServiceNumber="$(nslookup -timeout=10 release-name-sn-platform-broker | grep Name | wc -l)"; until [ ${brokerServiceNumber} -ge 1 ]; do
  echo "pulsar cluster release-name-sn-platform isn't initialized yet ... check in 10 seconds ...";
  sleep 10;
  brokerServiceNumber="$(nslookup -timeout=10 release-name-sn-platform-broker | grep Name | wc -l)";
done;

tokenLen=$(kubectl get secret release-name-sn-platform-vault-unseal-keys -o jsonpath='{.data.vault-root}'|wc -m); until [ ${tokenLen} -gt 1 ]; do
  echo "current token is empty ! wait another 10s~"
  sleep 10;
  tokenLen=$(kubectl get secret release-name-sn-platform-vault-unseal-keys -o jsonpath='{.data.vault-root}'|wc -m);
done; echo "vault root token is ready"; rootToken=$(kubectl get secret release-name-sn-platform-vault-unseal-keys -o jsonpath='{.data.vault-root}'|base64 -d); echo $rootToken; echo $rootToken > /vault/rootToken; export ROOT_TOKEN=$rootToken; vault login $ROOT_TOKEN; until [ $? -eq 0 ]; do
   echo "vault is not ready now, wait another 5s~";
   sleep 5;
   vault login $ROOT_TOKEN;
done; echo "vault is ready~";
cd /vault/pulsar/init_vault_streamnative_console && /usr/local/bin/zsh startup.sh; echo -ne "POST /quitquitquit HTTP/1.1\r\nHost: 127.0.0.1:15020\r\nUser-Agent: curl/7.68.0\r\nAccept: */*\r\n\r\n"|nc 127.0.0.1:15020 || true;
