command:
export current_mongodb_version=$(kubectl get deployment release-name-mongodb -o=jsonpath='{$.spec.template.metadata.labels}' -n default | awk -F'helm.sh/chart' '{print $2}' | awk -F'[^0-9]+' '{ print $2 }'); if [ "$current_mongodb_version" -eq "12" ];
  then kubectl scale deployment release-name-mongodb --replicas=0 -n default;
  else echo "MongoDB is up-to-date";
fi

export deployment_name=$(kubectl get deployments -n default -o custom-columns=NAME:.metadata.name | grep 'box'); export sts_name=$(kubectl get sts -n default -o custom-columns=NAME:.metadata.name | grep 'nats'); export current_nats_version=$(kubectl get sts $sts_name -o=jsonpath='{$.metadata.labels}' -n default | awk -F'chart' '{print $2}' | awk -F'[^0-9]+' '{ print $2"."$3"."$4 }');
if [ "$current_nats_version" = "0.19.1" ];
  then kubectl delete deployment $deployment_name -n default;
       kubectl delete sts $sts_name -n default;
  else echo "NATS version is up-to-date";
fi

NAMESPACE=default echo "Checking if namespace ${NAMESPACE} exists"
  if kubectl get namespaces ${NAMESPACE}
    then echo "Namespace already exists";
  else
    echo "creating namespace ${NAMESPACE}"
    kubectl create namespace ${NAMESPACE};
  fi
